import React, { useState, useEffect } from 'react';

const Tarea = (props) => {
    const [checked, setChecked] = useState();
    const [myclass, setClass] = useState('')

    useEffect(() => {
     if (checked) {
        setClass('pristine');
    } else {
        setClass('');
    }    
    }, [checked])

    const borrarTarea = () => {
        props.borrar(props.id);
    }

    return(
        <div className= "tarea" >
            <input type = "checkbox" 
            className=  {"cb " + myclass}

            onChange={() => {setChecked(checked)}
             } /> 
            <span className >{props.tarea}</span> 
             <span onClick ={borrarTarea}><i className="far fa-trash-alt"></i></span>
        </div>
    )
}

export default Tarea;
